Opcodo
Between Borders
Weather Mood
Birth Year Films
Calculator
*/
class Wrapper extends Component {
  constructor(props) {
    super(props);
    this.state = {
      1: {
        title: "Contendu",
        imgsrc: "test",
        description:
          "A marketplace for content marketers to be paired with students.<br /> Built a new front-end interface in HTML & CSS upon an existing Wordpress theme",
        technology: "Wordpress, HTML, PHP, Vanilla JS",
        hyperlink: "http://contendu.com"
      },
      2: {
        title: "OPCODO",
        imgsrc: "test",
        description: "An agency website. Build with Wordpress & HTML",
        technology: "Wordpress, HTML, CSS",
        hyperlink: "http://opcodo.com"
      },
      3: {
        title: "Between Borders",
        imgsrc: "test",
        description:
          "An online magazine celebrating diversity. Took an design brief and built with Wordpress & HTML.",
        technology: "Wordpress, HTML, CSS",
        hyperlink: "http://between-borders.com/"
      },
      4: {
        title: "Weather Mood",
        imgsrc: "test",
        description: "",
        technology: "",
        hyperlink: "http://google.com"
      },
      5: {
        title: "Birth Year Films",
        imgsrc: "test",
        description: "Test Description",
        technology: "",
        hyperlink: "http://google.com"
      },
      6: {
        title: "Calculator",
        imgsrc: "test",
        description: "Test Description",
        technology: "",
        hyperlink: "http://google.com"
      }
    };
  }
  render() {
    return (
      <div className="portfolio-wrapper">
        will thomas
        <br />
        front end developer
        <PorfolioItem title={this.state[1].title} />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
        <PorfolioItem />
      </div>
    );
  }
}

class PorfolioItem extends Component {
  render() {
    return <div>{this.props.title}</div>;
  }
}

export default Wrapper;
